package com.example.testunit.demo;

import com.example.testunit.utils.Base64Util;
import org.junit.Test;
import sun.misc.BASE64Decoder;

import javax.imageio.ImageIO;
import java.awt.image.BufferedImage;
import java.io.ByteArrayInputStream;
import java.io.File;
import java.io.IOException;

/**
 * @Despriction:
 * @Author: wangcheng
 * @Date: 2018/2/6 18:02
 */
public class TestBase64 {

    @Test
    public void Base64ToImgFile() {
        //正常的imgBase64
        //String base64Img = "";
        //nciic非正常的imgBase64
        String base64Img = "";

        try {
            BASE64Decoder base64Decoder = new sun.misc.BASE64Decoder();
            byte[] imgBytes = base64Decoder.decodeBuffer(Base64Util.imgBase64Transform(base64Img));
            //转换成jpg图片
            ByteArrayInputStream imgIs = new ByteArrayInputStream(imgBytes);
            BufferedImage imgBi = ImageIO.read(imgIs);
            ImageIO.write(imgBi,"jpg", new File("D:\\test\\img.jpg"));
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

}
